member.summary:
Base class for CommandSetterData classes.


internal class SetDemandValueData : BurCommandSetterData

{

   internal SetDemandValueData(string structName, string taskName)

       : base(structName, taskName)

   {

       Add(TubeCommandNames.PcDemandValue);

   }



   internal Value PcDemandValue

   {

       set { base[TubeCommandNames.PcDemandValue] = value; }

   }

}

